void mov_monstro1(){
	srand(time(NULL));
	switch ((rand() % 4) + 1){
		case 1:
			if (fase2[monster_i - 1][monster_j] == '&'){
				player_hp -= 10;
			} else if (fase2[monster_i - 1][monster_j] == ' '){
				fase2[monster_i][monster_j] = ' ';
                monster_i--;
				fase2[monster_i][monster_j] = 'X';
            } else {
				fase2[monster_i][monster_j] = ' ';
                monster_i++;
				fase2[monster_i][monster_j] = 'X';
			}
            break;
		
		case 2:
			if (fase2[monster_i][monster_j - 1] == '&'){
				player_hp -= 10;
			} else if ((fase2[monster_i][monster_j - 1] == ' ')){
				fase2[monster_i][monster_j] = ' ';
                monster_j--;
				fase2[monster_i][monster_j] = 'X';
            } else {
				fase2[monster_i][monster_j] = ' ';
                monster_j++;
				fase2[monster_i][monster_j] = 'X';
			}
			break;
		
		case 3:
			if (fase2[monster_i + 1][monster_j] == '&'){
				player_hp -= 10;
			} else if ((fase2[monster_i + 1][monster_j] == ' ')){
				fase2[monster_i][monster_j] = ' ';
                monster_i++;
				fase2[monster_i][monster_j] = 'X';
            } else {
				fase2[monster_i][monster_j] = ' ';
                monster_i--;
				fase2[monster_i][monster_j] = 'X';
			}
			break;
		
		case 4:
			if (fase2[monster_i][monster_j + 1] == '&'){
				player_hp -= 10;
			} else if ((fase2[monster_i][monster_j + 1] == ' ')){
				fase2[monster_i][monster_j] = ' ';
                monster_j++;
				fase2[monster_i][monster_j] = 'X';
            } else {
				fase2[monster_i][monster_j] = ' ';
                monster_j--;
				fase2[monster_i][monster_j] = 'X';
			}
			break;
	}
}

void mov_monstro2(){
	
	//LINHA
	if ((fase3[monster_i - 1][monster_j] == '&') || (fase3[monster_i + 1][monster_j] == '&')){
		player_hp -= 10;
	} else if ((fase3[monster_i - 1][monster_j] == ' ') && (monster_i - 1 != player_i) && (monster_i > player_i)){
		fase3[monster_i][monster_j] = ' ';
        monster_i--;
		fase3[monster_i][monster_j] = 'V';
    } else if ((fase3[monster_i + 1][monster_j] == ' ') && (monster_i + 1 != player_i) && (monster_i < player_i)) {
		fase3[monster_i][monster_j] = ' ';
        monster_i++;
		fase3[monster_i][monster_j] = 'V';
	}
	
	//COLUNA
	if ((fase3[monster_i][monster_j - 1] == '&') || (fase3[monster_i][monster_j + 1] == '&')){
		player_hp -= 10;
	} else if ((fase3[monster_i][monster_j - 1] == ' ') && (monster_j - 1 != player_j) && (monster_j > player_j)){
		fase3[monster_i][monster_j] = ' ';
        monster_j--;
		fase3[monster_i][monster_j] = 'V';
    } else if ((fase3[monster_i][monster_j + 1] == ' ') && (monster_j + 1 != player_j) && (monster_j < player_j)) {
		fase3[monster_i][monster_j] = ' ';
        monster_j++;
		fase3[monster_i][monster_j] = 'V';
	}
}

